import React, { useEffect, useState } from "react";
import {useSearchParams} from 'react-router-dom';
import axios from 'axios';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import NavBar from "../components/NavBar";

export const ItemState = ({phoneList}) =>{
    const [openDialogId, setOpenDialogId] = useState(null);
    const [reviewList, setReviewList] = useState([]);

    const handleClickOpen = (id) => {
     setOpenDialogId(id);
     const temList = phoneList.filter(i => i._id === id);
     //console.log(temList)
     setReviewList(temList);
     //console.log(reviewList)
    };

    const handleClose = () => {
     setOpenDialogId(null);
    };

    const [isExpanded, setIsExpanded] = useState(false);
    const handleShowMore = () => {
     setIsExpanded(true);
    };

    const [numDisplayed, setNumDisplayed] = useState(3);

      const handleShowMoreReviews = () => {
     setNumDisplayed(numDisplayed + 3);
     };

     return (
     <div>
     <div className="image-container">
     {phoneList.map((i)=> (
       <div key={i._id}>
        < img onClick={() => handleClickOpen(i._id)} src={`/${i.image}`} style={{ height: "100px", width: "100px" }}/>
        <Dialog open={openDialogId === i._id} onClose={handleClose}>
         <DialogTitle>Detail of Phone</DialogTitle>
         <DialogContent>
          <p>Title: {i.title}</p >
          <p>Brand: {i.brand}</p >
          < img src={`/${i.image}`} style={{ height: "100px", width: "100px" }}/>
          <p>Stock: {i.stock}</p >
          <p>Seller: {i.seller.firstname} {i.seller.lastname}</p >
          <p>Price: {i.price}</p >
         <div>
          {reviewList.map((item, index) => (
           item.reviews.slice(0, numDisplayed).map((review, i) => (
            <div key={i}>
             <p>Reviewer: {review.reviewer.firstname} {review.reviewer.lastname}</p >
             <p>Rating: {review.rating}</p >
             <div>

              <p>
               {isExpanded ? review.comment : review.comment.substring(0, 200)}
              </p >{review.comment.length > 200 && !isExpanded && (
               <button onClick={handleShowMore}>Show More</button>
              )}
             </div>
            </div>
           ))
          ))}
          <button onClick={handleShowMoreReviews}>Show More Reviews</button>
          <button>Add To Chart</button>
          <p>Current Added Quantity</p >
          <input
           type="text"
           placeholder="0"
          />
          <input
           type="text"
           placeholder="input comment"
          />
         </div>
         </DialogContent>
        </Dialog>
        <br />
        <span>title: {i.title}</span>
        <span style={{ fontSize: "20px", color: "red", padding: "0 20px" }}>
         price: {i.price}
        </span>
        <br />
       </div>
      ))}
     </div>
     </div>

   )}

export const Home = () => {
    //const [searchParams, setSearchParams] = useSearchParams();
    //const [status, setStatus] = useState('');
    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [brand, setBrand] = useState({ list: [] });
    const [soldOut, setsSoldOut] = useState([]);
    const [best, setBest] = useState([]);
    //let token = searchParams.get("token");
    useEffect(() => {
        axios.get('http://localhost:8080/api/home/home', {
        }).then(function (response) {
            const temp = response;
            const temp2 = temp.data;

            setData(temp2.data.list);
            setBrand(temp2.data.brands);
            setsSoldOut(temp2.data.listSoldOut);
            setBest(temp2.data.listBest);

        }).catch(function (error) {
            alert(error.response.data.message);
        });
    },[]);
    //console.log("res.data");
    // console.log("res.data", data);
    // console.log("best", best);
    // console.log("soldout", soldOut);





       return (
        <div>
         <div>
         <NavBar />
         </div>
         <div>
         <h2>Sold Out Soon: </h2>
         </div>
         <ItemState  phoneList={soldOut} />
         <div>
         <h2>Best Seller: </h2> 
         </div>
         <ItemState  phoneList={best} />

        </div>


        );

};
