import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import './Login.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';


export const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const signIn = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/auth/sign-in', {
            username: username,
            password: password
        }).then(function (response) {
            console.log(response);
        }).catch(function (error) {
            alert(error.response.data.message);
        });
    }
    return(
        <div className="login">
            <label for="username">username</label>
            <TextField id="outlined-basic" label="Outlined" variant="outlined" type="username" placeholder="username" value={username} onChange={(e) => setUsername(e.target.value)}/> 
            <label for="password">password</label>
            <TextField id="outlined-basic" label="Outlined" variant="outlined" type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)}/> 
            <Button variant="contained" onClick={signIn}>Login</Button>
            <Link to="/reset">Forgot your password?</Link>
            <Button variant="contained" onClick={() => {navigate("/setup")}}>Setup</Button>
        </div>
    );
};
